import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { Link } from 'react-router-dom';
import Rating from './Rating';
import axios from 'axios';
import { useContext } from 'react';
import { Store } from '../Store';
import '../index.css';
import ReactPaginate from "react-paginate";

function Product(props) {
  const { product } = props;

  const { state, dispatch: ctxDispatch } = useContext(Store);
  const {
    cart: { cartItems },
  } = state;

  const addToCartHandler = async (item) => {
    const existItem = cartItems.find((x) => x._id === product._id);// more understanding on this
    const quantity = existItem ? existItem.quantity + 1 : 1;
    const { data } = await axios.get(`/api/products/${item._id}`);
    if (data.countInStock < quantity) {
      window.alert('Sorry. Product is out of stock');
      return;
    }
    ctxDispatch({
      type: 'CART_ADD_ITEM',
      payload: { ...item, quantity },
    });
  };

  return (
    <>

    <Card>
      <Link to={`/product/${product.slug}`}>
        <img src={product.image} className="card-img-top" style={{width: '250px', height: '300px'}} alt={product.name} />
      </Link>
      <Card.Body className='underline'>
        <Link to={`/product/${product.slug}`}>
          <Card.Title className='underline nav-link'>{product.name}</Card.Title>
        </Link> 
        <Rating rating={product.rating} numReviews={product.numReviews} />
        <Card.Text>â‚¦ {product.price}</Card.Text>
        {product.countInStock === 0 ? (
          <Button variant="light" disabled>
            Out of stock
          </Button>
        ) : (
          <Button onClick={() => addToCartHandler(product)}>Add to cart</Button>
        )}
      </Card.Body>
    </Card>
    </>
  );
}
export default Product;
